import React from "react";
import ProductCard from "./ProductCard";

const mockProducts = [
  {
    id: 1,
    name: "Wireless Headphones",
    price: 45.99,
    description: "High-quality sound with noise cancellation and long battery life.",
  },
  {
    id: 2,
    name: "Smartwatch",
    price: 79.99,
    description: "Track fitness, receive calls, and monitor heart rate with ease.",
  },
  {
    id: 3,
    name: "Bluetooth Speaker",
    price: 34.5,
    description: "Portable speaker with strong bass and 12-hour battery life.",
  },
  {
    id: 4,
    name: "USB-C Charger",
    price: 15.0,
    description: "Fast 20W charging adapter compatible with most devices.",
  },
];

export default function ProductList({ searchTerm, onAddToCart, onViewProduct }) {
  const filtered = mockProducts.filter((p) =>
    p.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="product-grid">
      {filtered.length === 0 ? (
        <p>No products found.</p>
      ) : (
        filtered.map((product) => (
          <ProductCard
            key={product.id}
            product={product}
            onAddToCart={onAddToCart}
            onView={() => onViewProduct(product)}
          />
        ))
      )}
    </div>
  );
}
