import React, { useState } from "react";
import ProductList from "./components/ProductList";
import ProductDetails from "./components/ProductDetails";
import CartModal from "./components/CartModal";
import "./App.css";

export default function App() {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [cartItems, setCartItems] = useState([]);
  const [showCart, setShowCart] = useState(false);
  const [orders, setOrders] = useState([]); // 🧾 store all orders
  const [activeOrder, setActiveOrder] = useState(null); // currently viewed order

  // Add item to cart
  const handleAddToCart = (product, qty = 1) => {
    setCartItems((prev) => {
      const existing = prev.find((item) => item.id === product.id);
      if (existing) {
        return prev.map((item) =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + qty }
            : item
        );
      }
      return [...prev, { ...product, quantity: qty }];
    });
  };

  // Checkout (creates a new order and shows payment modal)
  const handleCheckout = () => {
    const total = cartItems.reduce(
      (sum, item) => sum + item.price * item.quantity,
      0
    );
    const newOrder = {
      orderId: "ORD-" + Math.floor(Math.random() * 10000),
      items: cartItems,
      total: total.toFixed(2),
      status: "PENDING",
    };

    setOrders((prev) => [...prev, newOrder]);
    setActiveOrder(newOrder);
    setCartItems([]);
    setShowCart(false);
  };

  // Simulate payment confirmation
  const confirmPayment = () => {
    const updated = {
      ...activeOrder,
      status: "CONFIRMED",
    };

    setOrders((prev) =>
      prev.map((o) => (o.orderId === activeOrder.orderId ? updated : o))
    );
    setActiveOrder(updated);
  };

  const categories = ["All", "Electronics", "Fashion", "Home"];

  return (
    <div className="App">
      {/* HEADER */}
      <header className="header">
        <h1>🛍️ MyShop</h1>
        <input
          type="text"
          placeholder="Search products..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button className="cart-icon" onClick={() => setShowCart(true)}>
          🛒 {cartItems.length}
        </button>
      </header>

      {/* CATEGORY FILTER */}
      <div className="category-bar">
        {categories.map((cat) => (
          <button
            key={cat}
            className={`category-btn ${selectedCategory === cat ? "active" : ""}`}
            onClick={() => setSelectedCategory(cat)}
          >
            {cat}
          </button>
        ))}
      </div>

      {/* PRODUCT GRID */}
      <ProductList
        searchTerm={searchTerm}
        selectedCategory={selectedCategory}
        onViewProduct={setSelectedProduct}
      />

      {/* PRODUCT DETAILS */}
      {selectedProduct && (
        <ProductDetails
          product={selectedProduct}
          onClose={() => setSelectedProduct(null)}
          onAddToCart={handleAddToCart}
        />
      )}

      {/* CART MODAL */}
      {showCart && (
        <CartModal
          cartItems={cartItems}
          onClose={() => setShowCart(false)}
          onCheckout={handleCheckout}
        />
      )}

      {/* PAYMENT + ORDER STATUS */}
      {activeOrder && (
        <div className="payment-modal">
          <div className="payment-content">
            <h2>💳 Confirm Your Payment</h2>
            <p>
              <strong>Order ID:</strong> {activeOrder.orderId}
            </p>
            <p>
              <strong>Total:</strong> ${activeOrder.total}
            </p>
            <p>
              <strong>Status:</strong>{" "}
              <span
                className={
                  activeOrder.status === "CONFIRMED" ? "confirmed" : "pending"
                }
              >
                {activeOrder.status}
              </span>
            </p>

            {activeOrder.status === "PENDING" ? (
              <button className="confirm-btn" onClick={confirmPayment}>
                Confirm Payment
              </button>
            ) : (
              <button
                className="close-btn"
                onClick={() => setActiveOrder(null)}
              >
                Close
              </button>
            )}
          </div>
        </div>
      )}

      {/* ORDER TRACKER */}
      {orders.length > 0 && (
        <div className="order-tracker">
          <h3>📦 Your Orders</h3>
          {orders.map((order) => (
            <div key={order.orderId} className="order-item">
              <p>
                <strong>ID:</strong> {order.orderId} |{" "}
                <strong>Status:</strong> {order.status} |{" "}
                <strong>Total:</strong> ${order.total}
              </p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
