import React, { useState } from "react";
import ProductList from "./components/ProductList";
import OrderStatus from "./components/OrderStatus";
import CartModal from "./components/CartModal";
import "./App.css";

function App() {
  const [orderStatus, setOrderStatus] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [cartItems, setCartItems] = useState([]);
  const [showCart, setShowCart] = useState(false);

  const handlePlaceOrder = async () => {
    // Simulate API (for Week 2)
    const response = { orderId: "ORD-001", status: "PENDING" };
    setOrderStatus(response);
  };

  const handleSearch = (e) => setSearchTerm(e.target.value);

  const addToCart = (product) => {
    setCartItems((prev) => [...prev, product]);
  };

  const toggleCart = () => setShowCart(!showCart);

  const clearCart = () => setCartItems([]);

  return (
    <div className="app">
      {/* Navbar */}
      <header className="navbar">
        <h1 className="logo">ShopMate</h1>
        <div className="search-container">
          <input
            type="text"
            placeholder="Search products..."
            value={searchTerm}
            onChange={handleSearch}
          />
        </div>
      </header>

      {/* Product Section */}
      <main>
        <h2>Available Products</h2>
        <ProductList searchTerm={searchTerm} onAddToCart={addToCart} />
        <button className="order-btn" onClick={handlePlaceOrder}>
          Place Order
        </button>
        {orderStatus && <OrderStatus data={orderStatus} />}
      </main>

      {/* Floating Cart */}
      <div className="floating-cart" onClick={toggleCart}>
        ðŸ›’
        {cartItems.length > 0 && (
          <span className="cart-count">{cartItems.length}</span>
        )}
      </div>

      {/* Cart Modal */}
      {showCart && (
        <CartModal
          items={cartItems}
          onClose={toggleCart}
          onClear={clearCart}
        />
      )}

      <footer className="footer">Â© 2025 ShopMate</footer>
    </div>
  );
}

export default App;
