import React, { useMemo } from "react";
import ProductCard from "./ProductCard";

const mockProducts = [
  // Electronics
  {
    id: 1,
    name: "Wireless Bluetooth Headphones",
    category: "Electronics",
    price: 79.99,
    description: "Premium noise-cancelling headphones with 30-hour battery life.",
    image: "https://images.unsplash.com/photo-1505740420928-5e560c06d30e?w=500&auto=format&fit=crop&q=60",
  },
  {
    id: 2,
    name: "Smart Watch Series 5",
    category: "Electronics",
    price: 199.99,
    description: "Advanced smartwatch with health monitoring and GPS.",
    image: "https://images.unsplash.com/photo-1523275335684-37898b6baf30?w=500&auto=format&fit=crop&q=60",
  },
  {
    id: 3,
    name: "iPhone 14 Pro",
    category: "Electronics",
    price: 999.99,
    description: "Latest iPhone with advanced camera and A16 chip.",
    image: "https://images.unsplash.com/photo-1592899677977-9c10ca588bbd?w=500&auto=format&fit=crop&q=60",
  },

  // Fashion
  {
    id: 4,
    name: "Classic White Sneakers",
    category: "Fashion",
    price: 89.99,
    description: "Comfortable leather sneakers for everyday wear.",
    image: "https://images.unsplash.com/photo-1549298916-b41d501d3772?w=500&auto=format&fit=crop&q=60",
  },
  {
    id: 5,
    name: "Denim Jacket",
    category: "Fashion",
    price: 59.99,
    description: "Vintage-style denim jacket for all seasons.",
    image: "https://images.unsplash.com/photo-1551028719-00167b16eac5?w=500&auto=format&fit=crop&q=60",
  },

  // Home
  {
    id: 6,
    name: "Ceramic Coffee Mug Set",
    category: "Home",
    price: 24.99,
    description: "Set of 4 beautiful ceramic mugs, dishwasher safe.",
    image: "https://images.unsplash.com/photo-1514228742587-6b1558fcf93a?w=500&auto=format&fit=crop&q=60",
  },
  {
    id: 7,
    name: "Modern Desk Lamp",
    category: "Home",
    price: 45.99,
    description: "LED desk lamp with adjustable brightness.",
    image: "https://images.unsplash.com/photo-1507473885765-e6ed057f782c?w=500&auto=format&fit=crop&q=60",
  },

  // Books
  {
    id: 8,
    name: "The Great Gatsby",
    category: "Books",
    price: 12.99,
    description: "Classic novel by F. Scott Fitzgerald.",
    image: "https://images.unsplash.com/photo-1544947950-fa07a98d237f?w=500&auto=format&fit=crop&q=60",
  },

  // Sports
  {
    id: 9,
    name: "Basketball",
    category: "Sports",
    price: 29.99,
    description: "Official size basketball for indoor and outdoor play.",
    image: "https://images.unsplash.com/photo-1546519638-68e109498ffc?w=500&auto=format&fit=crop&q=60",
  }
];

export default function ProductList({ searchTerm, selectedCategory, onViewProduct }) {
  const filteredProducts = useMemo(() => {
    return mockProducts.filter(
      (p) =>
        p.name.toLowerCase().includes(searchTerm.toLowerCase()) &&
        (selectedCategory === "All" || p.category === selectedCategory)
    );
  }, [searchTerm, selectedCategory]);

  if (filteredProducts.length === 0) {
    return (
      <div className="no-products">
        <h3>No products found</h3>
        <p>Try adjusting your search or filter criteria</p>
      </div>
    );
  }

  return (
    <div className="product-grid">
      {filteredProducts.map((product) => (
        <ProductCard 
          key={product.id} 
          product={product} 
          onView={() => onViewProduct(product)} 
        />
      ))}
    </div>
  );
}
