import React, { useState } from "react";

export default function Payment({ order, onClose }) {
  const [paid, setPaid] = useState(false);

  const handlePayment = () => {
    // Simulate payment processing
    setTimeout(() => {
      setPaid(true);
    }, 1500);
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <button className="close-btn" onClick={onClose}>✕</button>

        {!paid ? (
          <>
            <h2>Complete Your Purchase</h2>
            <div className="order-summary">
              <p><strong>Order ID:</strong> {order.orderId}</p>
              <p><strong>Status:</strong> <span className="status-pending">Pending Payment</span></p>

              <div className="order-items">
                <h4>Order Items:</h4>
                {order.items.map((item, index) => (
                  <div key={index} className="order-item">
                    <img src={item.image} alt={item.name} className="order-item-img" />
                    <div className="order-item-details">
                      <h5>{item.name}</h5>
                      <p>Quantity: {item.quantity}</p>
                      <p>${item.price.toFixed(2)} each</p>
                    </div>
                  </div>
                ))}
              </div>

              <div className="order-total">
                <h3>Total: ${order.total}</h3>
              </div>
            </div>

            <div className="payment-actions">
              <button className="btn primary" onClick={handlePayment}>
                Pay ${order.total}
              </button>
              <button className="btn secondary" onClick={onClose}>
                Cancel
              </button>
            </div>
          </>
        ) : (
          <>
            <div className="payment-success">
              <h2>✅ Payment Successful!</h2>
              <p>Your order has been placed successfully.</p>
              <div className="success-details">
                <p><strong>Order ID:</strong> {order.orderId}</p>
                <p><strong>Status:</strong> <span className="status-completed">COMPLETED</span></p>
                <p><strong>Total Paid:</strong> ${order.total}</p>
              </div>
              <button className="btn primary" onClick={onClose}>
                Continue Shopping
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
}
