import React from "react";
import ProductCard from "./ProductCard";

const mockProducts = [
  { id: 1, name: "Wireless Headphones", price: 45.99 },
  { id: 2, name: "Smartwatch", price: 79.99 },
  { id: 3, name: "Bluetooth Speaker", price: 34.5 },
  { id: 4, name: "USB-C Charger", price: 15.0 },
  { id: 5, name: "Phone Case", price: 10.0 },
  { id: 6, name: "Laptop Stand", price: 29.0 },
];

export default function ProductList({ searchTerm, onAddToCart }) {
  const filtered = mockProducts.filter((p) =>
    p.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="product-grid">
      {filtered.length === 0 ? (
        <p>No products found.</p>
      ) : (
        filtered.map((product) => (
          <ProductCard
            key={product.id}
            product={product}
            onAddToCart={() => onAddToCart(product)}
          />
        ))
      )}
    </div>
  );
}
