import React, { useState, useCallback, useMemo } from "react";
import CategoryBar from "./components/CategoryBar";
import ProductList from "./components/ProductList";
import ProductDetails from "./components/ProductDetails";
import CartModal from "./components/CartModal";
import Payment from "./components/Payment";
import "./App.css";

export default function App() {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [cartItems, setCartItems] = useState([]);
  const [showCart, setShowCart] = useState(false);
  const [order, setOrder] = useState(null);

  const handleAddToCart = useCallback((product, qty = 1) => {
    setCartItems((prev) => {
      const existing = prev.find((item) => item.id === product.id);
      if (existing) {
        return prev.map((item) =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + qty }
            : item
        );
      }
      return [...prev, { ...product, quantity: qty }];
    });
  }, []);

  const handleRemoveFromCart = useCallback((productId) => {
    setCartItems(prev => prev.filter(item => item.id !== productId));
  }, []);

  const handleUpdateQuantity = useCallback((productId, newQuantity) => {
    if (newQuantity < 1) {
      handleRemoveFromCart(productId);
      return;
    }
    setCartItems(prev => 
      prev.map(item => 
        item.id === productId ? { ...item, quantity: newQuantity } : item
      )
    );
  }, [handleRemoveFromCart]);

  const cartItemCount = useMemo(() => 
    cartItems.reduce((count, item) => count + item.quantity, 0),
    [cartItems]
  );

  const cartTotal = useMemo(() => 
    cartItems.reduce((sum, item) => sum + (item.price * item.quantity), 0).toFixed(2),
    [cartItems]
  );

  const handleCheckout = useCallback(() => {
    if (cartItems.length === 0) return;
    const orderData = { 
      orderId: "ORD-" + Math.floor(Math.random() * 10000), 
      status: "PENDING",
      items: [...cartItems],
      total: cartTotal
    };
    setOrder(orderData);
    setCartItems([]);
    setShowCart(false);
  }, [cartItems, cartTotal]);

  return (
    <div className="App">
      <header className="header">
        <h1>üõçÔ∏è MyShop</h1>
        <input
          type="text"
          placeholder="Search products..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </header>

      <CategoryBar
        categories={["All", "Electronics", "Fashion", "Home"]}
        selected={selectedCategory}
        onSelect={setSelectedCategory}
      />

      <ProductList
        searchTerm={searchTerm}
        selectedCategory={selectedCategory}
        onViewProduct={setSelectedProduct}
      />

      {/* Floating Shopping Cart Icon */}
      <div className="floating-cart" onClick={() => setShowCart(true)}>
        üõí
        {cartItemCount > 0 && (
          <span className="cart-count">{cartItemCount}</span>
        )}
      </div>

      {selectedProduct && (
        <ProductDetails
          product={selectedProduct}
          onClose={() => setSelectedProduct(null)}
          onAddToCart={handleAddToCart}
        />
      )}

      {showCart && (
        <CartModal
          cartItems={cartItems}
          onClose={() => setShowCart(false)}
          onCheckout={handleCheckout}
          onUpdateQuantity={handleUpdateQuantity}
          onRemoveItem={handleRemoveFromCart}
          total={cartTotal}
        />
      )}

      {order && (
        <Payment order={order} onClose={() => setOrder(null)} />
      )}
    </div>
  );
}
