import React, { useState } from "react";
import CategoryBar from "./components/CategoryBar";
import ProductList from "./components/ProductList";
import ProductDetails from "./components/ProductDetails";
import CartModal from "./components/CartModal";
import Payment from "./components/Payment";
import OrderStatus from "./components/OrderStatus";
import "./App.css";

export default function App() {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [cartItems, setCartItems] = useState([]);
  const [showCart, setShowCart] = useState(false);
  const [order, setOrder] = useState(null);

  const handleAddToCart = (product, qty = 1) => {
    setCartItems((prev) => {
      const existing = prev.find((item) => item.id === product.id);
      if (existing) {
        return prev.map((item) =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + qty }
            : item
        );
      }
      return [...prev, { ...product, quantity: qty }];
    });
  };

  const handleBuyNow = (order) => setOrder(order);

  return (
    <div className="App">
      <header className="header">
        <h1>üõçÔ∏è MyShop</h1>
        <input
          type="text"
          placeholder="Search products..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button className="cart-icon" onClick={() => setShowCart(true)}>
          üõí {cartItems.length}
        </button>
      </header>

      <CategoryBar
        categories={["All", "Electronics", "Fashion", "Home"]}
        selected={selectedCategory}
        onSelect={setSelectedCategory}
      />

      <ProductList
        searchTerm={searchTerm}
        selectedCategory={selectedCategory}
        onViewProduct={setSelectedProduct}
      />

      {selectedProduct && (
        <ProductDetails
          product={selectedProduct}
          onClose={() => setSelectedProduct(null)}
          onAddToCart={handleAddToCart}
          onBuyNow={handleBuyNow}
        />
      )}

      {showCart && (
        <CartModal
          cartItems={cartItems}
          onClose={() => setShowCart(false)}
          onCheckout={() => setOrder({ orderId: "ORD-" + Math.floor(Math.random() * 1000), status: "PENDING" })}
        />
      )}

      {order && (
        <Payment order={order} onClose={() => setOrder(null)} />
      )}
    </div>
  );
}
