// src/components/OrderPayment.jsx
import React, { useState } from "react";

const OrderPayment = ({ cartItems, onComplete }) => {
  const [order, setOrder] = useState(null); // stores order info
  const [status, setStatus] = useState("PENDING"); // payment status

  // Generate order and total
  const handleCreateOrder = () => {
    const total = cartItems.reduce(
      (sum, item) => sum + item.price * item.quantity,
      0
    );

    const newOrder = {
      orderId: "ORD-" + Math.floor(Math.random() * 1000000),
      items: cartItems,
      total: total.toFixed(2),
    };

    setOrder(newOrder);
    setStatus("PENDING");
  };

  // Simulate payment confirmation
  const handleConfirmPayment = () => {
    setStatus("CONFIRMED");
    onComplete(order.orderId, "CONFIRMED");
  };

  return (
    <div className="order-payment-container">
      {!order ? (
        <div>
          <h2>Review Your Order</h2>
          {cartItems.length === 0 ? (
            <p>Your cart is empty.</p>
          ) : (
            <>
              <ul>
                {cartItems.map((item) => (
                  <li key={item.id}>
                    {item.name} x {item.quantity} - ${item.price.toFixed(2)}
                  </li>
                ))}
              </ul>
              <button onClick={handleCreateOrder}>Proceed to Payment</button>
            </>
          )}
        </div>
      ) : (
        <div>
          <h2>Order Payment</h2>
          <p>
            <strong>Order ID:</strong> {order.orderId}
          </p>
          <p>
            <strong>Total:</strong> ${order.total}
          </p>
          <p>
            <strong>Status:</strong>{" "}
            <span className={status === "CONFIRMED" ? "confirmed" : "pending"}>
              {status}
            </span>
          </p>

          {status === "PENDING" && (
            <button onClick={handleConfirmPayment}>Confirm Payment</button>
          )}
        </div>
      )}
    </div>
  );
};

export default OrderPayment;
