import React, { useState } from "react";
import ProductList from "./components/ProductList";
import CartModal from "./components/CartModal";
import Payment from "./components/Payment";
import ProductDetails from "./components/ProductDetails";
import "./App.css";

function App() {
  const [searchTerm, setSearchTerm] = useState("");
  const [cartItems, setCartItems] = useState([]);
  const [showCart, setShowCart] = useState(false);
  const [showPayment, setShowPayment] = useState(false);
  const [orderData, setOrderData] = useState(null);
  const [selectedProduct, setSelectedProduct] = useState(null);

  const handleSearch = (e) => setSearchTerm(e.target.value);
  const toggleCart = () => setShowCart(!showCart);

  const addToCart = (product, quantity = 1) => {
    const items = Array(quantity).fill(product);
    setCartItems((prev) => [...prev, ...items]);
  };

  const clearCart = () => setCartItems([]);

  const openPayment = (order) => {
    setOrderData(order);
    setShowPayment(true);
  };

  const closePayment = () => {
    setShowPayment(false);
    clearCart();
  };

  const openProductDetails = (product) => setSelectedProduct(product);
  const closeProductDetails = () => setSelectedProduct(null);

  return (
    <div className="app">
      {/* Navbar */}
      <header className="navbar">
        <h1 className="logo">ShopMate</h1>
        <div className="search-container">
          <input
            type="text"
            placeholder="Search products..."
            value={searchTerm}
            onChange={handleSearch}
          />
        </div>
      </header>

      {/* Product Section */}
      <main>
        <h2>Available Products</h2>
        <ProductList
          searchTerm={searchTerm}
          onAddToCart={addToCart}
          onViewProduct={openProductDetails}
        />
      </main>

      {/* Floating Cart */}
      <div className="floating-cart" onClick={toggleCart}>
        ðŸ›’
        {cartItems.length > 0 && (
          <span className="cart-count">{cartItems.length}</span>
        )}
      </div>

      {/* Modals */}
      {showCart && (
        <CartModal
          items={cartItems}
          onClose={toggleCart}
          onProceedToPayment={openPayment}
        />
      )}

      {showPayment && orderData && (
        <Payment orderData={orderData} onClose={closePayment} />
      )}

      {selectedProduct && (
        <ProductDetails
          product={selectedProduct}
          onClose={closeProductDetails}
          onAddToCart={addToCart}
          onBuyNow={openPayment}
        />
      )}

      <footer className="footer">Â© 2025 ShopMate</footer>
    </div>
  );
}

export default App;
