import React from "react";
import ProductCard from "./ProductCard";

const mockProducts = [
  // Electronics
  {
    id: 1,
    name: "Wireless Headphones",
    category: "Electronics",
    price: 45.99,
    description: "High-quality sound with noise cancellation.",
    image: "https://images.unsplash.com/photo-1585386959984-a41552231685?w=600",
  },
  {
    id: 2,
    name: "Smartwatch",
    category: "Electronics",
    price: 79.99,
    description: "Track fitness, receive calls, and monitor heart rate.",
    image: "https://images.unsplash.com/photo-1603791452906-cb925bcf1a2c?w=600",
  },
  {
    id: 3,
    name: "Bluetooth Speaker",
    category: "Electronics",
    price: 34.5,
    description: "Portable speaker with rich bass and long battery life.",
    image: "https://images.unsplash.com/photo-1585386957449-479b8f8a3b7f?w=600",
  },

  // Fashion
  {
    id: 4,
    name: "Classic T-Shirt",
    category: "Fashion",
    price: 15.99,
    description: "Soft cotton t-shirt, available in multiple colors.",
    image: "https://images.unsplash.com/photo-1521334884684-d80222895322?w=600",
  },
  {
    id: 5,
    name: "Denim Jacket",
    category: "Fashion",
    price: 49.99,
    description: "Trendy and durable denim jacket for all seasons.",
    image: "https://images.unsplash.com/photo-1503341455253-b2e723bb3dbb?w=600",
  },
  {
    id: 6,
    name: "Sneakers",
    category: "Fashion",
    price: 59.99,
    description: "Comfortable sneakers with excellent grip.",
    image: "https://images.unsplash.com/photo-1528701800489-20be7c1a4d2b?w=600",
  },

  // Home & Living
  {
    id: 7,
    name: "Coffee Mug Set",
    category: "Home",
    price: 25.5,
    description: "Ceramic mugs perfect for coffee or tea lovers.",
    image: "https://images.unsplash.com/photo-1616628182501-1b8b1a49df2b?w=600",
  },
  {
    id: 8,
    name: "Wall Clock",
    category: "Home",
    price: 20.0,
    description: "Minimalist wall clock that fits any interior.",
    image: "https://images.unsplash.com/photo-1616628007266-503331c14b8d?w=600",
  },
  {
    id: 9,
    name: "Scented Candles",
    category: "Home",
    price: 18.99,
    description: "Relaxing aromatic candles for cozy evenings.",
    image: "https://images.unsplash.com/photo-1556228578-056d5a38a7e8?w=600",
  },
];

export default function ProductList({ searchTerm, selectedCategory, onViewProduct }) {
  const filtered = mockProducts.filter(
    (p) =>
      p.name.toLowerCase().includes(searchTerm.toLowerCase()) &&
      (selectedCategory === "All" || p.category === selectedCategory)
  );

  return (
    <div className="product-grid">
      {filtered.length === 0 ? (
        <p>No products found.</p>
      ) : (
        filtered.map((product) => (
          <ProductCard key={product.id} product={product} onView={() => onViewProduct(product)} />
        ))
      )}
    </div>
  );
}
