import React, { useState } from "react";
import ProductList from "./components/ProductList";
import ProductDetails from "./components/ProductDetails";
import CartModal from "./components/CartModal";
import OrderPayment from "./components/OrderPayment";
import "./App.css";

export default function App() {
  const [cartItems, setCartItems] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [showCart, setShowCart] = useState(false);
  const [showOrderPayment, setShowOrderPayment] = useState(false);

  const handleAddToCart = (product, qty = 1) => {
    setCartItems((prev) => {
      const existing = prev.find((item) => item.id === product.id);
      if (existing) {
        return prev.map((item) =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + qty }
            : item
        );
      }
      return [...prev, { ...product, quantity: qty }];
    });
  };

  const handleCheckout = () => {
    setShowCart(false);
    setShowOrderPayment(true);
  };

  const handlePaymentComplete = (orderId, status) => {
    alert(`Order ${orderId} is ${status}`);
    setCartItems([]);
    setShowOrderPayment(false);
  };

  return (
    <div className="App">
      <header>
        <h1>üõçÔ∏è MyShop</h1>
        <button onClick={() => setShowCart(true)}>Cart ({cartItems.length})</button>
      </header>

      {!showCart && !showOrderPayment && !selectedProduct && (
        <ProductList onViewProduct={setSelectedProduct} />
      )}

      {selectedProduct && (
        <ProductDetails
          product={selectedProduct}
          onClose={() => setSelectedProduct(null)}
          onAddToCart={handleAddToCart}
        />
      )}

      {showCart && (
        <CartModal
          cartItems={cartItems}
          onClose={() => setShowCart(false)}
          onCheckout={handleCheckout}
        />
      )}

      {showOrderPayment && (
        <OrderPayment cartItems={cartItems} onComplete={handlePaymentComplete} />
      )}
    </div>
  );
}
