import React, { useState } from "react";

export default function Payment({ order, onClose }) {
  const [paymentStatus, setPaymentStatus] = useState(null); // null, 'success', or 'failed'
  const [isProcessing, setIsProcessing] = useState(false);

  const handlePayment = () => {
    setIsProcessing(true);

    // Simulate payment processing with random outcome
    setTimeout(() => {
      const isSuccessful = Math.random() > 0.; 
      setPaymentStatus(isSuccessful ? 'success' : 'failed');
      setIsProcessing(false);
    }, 2000);
  };

  const handleRetry = () => {
    setPaymentStatus(null);
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <button className="close-btn" onClick={onClose}>✕</button>

        {!paymentStatus ? (
          <>
            <h2>Complete Your Purchase</h2>
            <div className="order-summary">
              <p><strong>Order ID:</strong> {order.orderId}</p>
              <p><strong>Status:</strong> <span className="status-pending">Pending Payment</span></p>

              <div className="order-items">
                <h4>Order Items:</h4>
                {order.items.map((item, index) => (
                  <div key={index} className="order-item">
                    <img src={item.image} alt={item.name} className="order-item-img" />
                    <div className="order-item-details">
                      <h5>{item.name}</h5>
                      <p>Quantity: {item.quantity}</p>
                      <p>${item.price.toFixed(2)} each</p>
                    </div>
                  </div>
                ))}
              </div>

              <div className="order-total">
                <h3>Total: ${order.total}</h3>
              </div>
            </div>

            <div className="payment-actions">
              <button 
                className={`btn primary ${isProcessing ? 'processing' : ''}`} 
                onClick={handlePayment}
                disabled={isProcessing}
              >
                {isProcessing ? (
                  <>
                    <span className="spinner"></span>
                    Processing...
                  </>
                ) : (
                  `Pay $${order.total}`
                )}
              </button>
              <button className="btn secondary" onClick={onClose} disabled={isProcessing}>
                Cancel
              </button>
            </div>
          </>
        ) : paymentStatus === 'success' ? (
          <div className="payment-result success">
            <h2>✅ Payment Successful!</h2>
            <p>Your order has been placed successfully.</p>
            <div className="result-details">
              <p><strong>Order ID:</strong> {order.orderId}</p>
              <p><strong>Status:</strong> <span className="status-completed">COMPLETED</span></p>
              <p><strong>Total Paid:</strong> ${order.total}</p>
              <p><strong>Items:</strong> {order.items.length} item(s)</p>
            </div>
            <button className="btn primary" onClick={onClose}>
              Continue Shopping
            </button>
          </div>
        ) : (
          <div className="payment-result failed">
            <h2>❌ Payment Failed</h2>
            <p>We couldn't process your payment. Please try again.</p>
            <div className="result-details">
              <p><strong>Order ID:</strong> {order.orderId}</p>
              <p><strong>Status:</strong> <span className="status-failed">FAILED</span></p>
              <p><strong>Reason:</strong> {getRandomFailureReason()}</p>
            </div>
            <div className="payment-actions">
              <button className="btn primary" onClick={handleRetry}>
                Try Again
              </button>
              <button className="btn secondary" onClick={onClose}>
                Cancel Order
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

// Helper function to generate random failure reasons
function getRandomFailureReason() {
  const reasons = [
    "Network error",
    "Transaction timeout",
  ];
  return reasons[Math.floor(Math.random() * reasons.length)];
}
